import React, {
  Component
} from 'react';
import {
  render
} from 'react-dom';
import AceEditor from 'react-ace';
import brace from 'brace';
import 'brace/mode/java';
import 'brace/mode/javascript';
import 'brace/theme/github';
import tools from 'brace/ext/language_tools';
import cppCodeCompleter from 'brace/ext/cpp-code';
import monokai from 'brace/theme/monokai'

class App extends Component {
  render() {
    return (
      <div className="App">
        <AceEditor
          mode="javascript"
          theme="monokai"
          name="blah1"
          onLoad={this.onLoad}
          onChange={this.Change}
          showGutter={true}
          height="800px"
          width="600px"
          fontSize="15px"
          showPrintMargin="true"
          wrapEnabled="true"
         
        />
      </div>
    );
  }
}
export default App;
